mapper.update:
UPDATE basket_spot
SET
    court_type = #{courtType}
    , fee_yn = #{feeYn}
    , court_size = #{courtSize}
    , goal_post = #{goalPost}

    , park_yn = #{parkYn}
    , modify_date = NOW()
    , court_name = #{courtName}
    , comment = #{comment}
    , has_light = #{hasLight}
    , light_time = #{lightTime}
    , open_status = #{openStatus}
    , open_time = #{openTime}
WHERE
    id = #{id}
