mapper.insert:
INSERT INTO feedback(
    fd_comment
    , fd_rating
) VALUES(
    #{fdComment}
    , #{fdRating}
)

INSERT INTO basket_spot (
    lat
    , lon
    , court_type
    , fee_yn
    , court_size
    , goal_post

    , park_yn
    , create_date
    , address
    , comment
    , court_name
) VALUES (
    #{lat}
    , #{lon}
    , #{courtType}
    , #{feeYn}
    , #{courtSize}
    , #{goalPost}

    , #{parkYn}
    , NOW()
    , #{address}
    , #{comment}
    , #{courtName}
)

mapper.update:
UPDATE basket_spot
SET
    court_type = #{courtType}
    , fee_yn = #{feeYn}
    , court_size = #{courtSize}
    , goal_post = #{goalPost}

    , park_yn = #{parkYn}
    , modify_date = NOW()
    , court_name = #{courtName}
    , comment = #{comment}
WHERE
    id = #{id}
