mapper.insert:
INSERT INTO basket_spot (
    lat
    , lon
    , court_type
    , fee_yn
    , court_size
    , goal_post
    , shower_yn
    , park_yn
    , create_date
) VALUES (
    #{lat}
    , #{lon}
    , #{courtType,jdbcType=VARCHAR}
    , #{feeYn}
    , #{courtSize,jdbcType=VARCHAR}
    , #{goalPost}
    , #{showerYn}
    , #{parkYn}
    , NOW()
)

mapper.update:
UPDATE basket_spot
SET
    court_type = #{courtType}
    , fee_yn = #{feeYn}
    , court_size = #{courtSize}
    , goal_post = #{goalPost}
    , shower_yn = #{showerYn}
    , park_yn = #{parkYn}
    , modify_date = NOW()
WHERE
    lat = #{lat}
AND lon = #{lon}

mapper.delete:
DELETE FROM basket_spot
WHERE
    lat = #{lat}
AND lon = #{lon}
